ย ย birthDate: string;
ย ย condition: string;
ย ย restriction: string;
ย ย syncedAt: string | null;
}

/**
ย* Interface de Leitura adaptada para o uso da propriedade 'timestamp'
ย* para importaรงรฃo de arquivos (usada no fileParsingService).
ย*/
