ย ย id: string;
ย ย // Campo usado para salvar no SQLite (ISO string)
ย ย measurement_time: string; 
ย ย // Novo campo: Usado pelo parser de arquivos (milissegundos UNIX)
ย ย timestamp: number; 
ย ย glucose_level: number;
ย ย meal_context: string | null;
ย ย time_since_meal: string | null;
ย ย notes: string | null;
ย ย syncedAt: string | null; 
}

// ----------------------
// FUNรรES DE SERVIรO BรSICAS
// ----------------------

/**
ย* Retorna instรขncia รบnica do DB
ย*/
export function getDB(): SQLite.Database {
ย ย if (!dbInstance) {
ย ย ย ย dbInstance = SQLite.openDatabase(DB_NAME);
ย ย }
ย ย return dbInstance;
}

/**
ย* Inicializa tabelas do banco
ย*/
export async function initDB(): Promise<boolean> {
ย ย return new Promise((resolve, reject) => {
ย ย ย ย const database = getDB();

ย ย ย ย database.transaction(
ย ย ย ย ย ย (tx: SQLite.SQLTransaction) => { // Tx tipado com SQLite.SQLTransaction
ย ย ย ย ย ย ย ย // Usuรกrios
ย ย ย ย ย ย ย ย tx.executeSql(
ย ย ย ย ย ย ย ย ย ย `CREATE TABLE IF NOT EXISTS users (
ย ย ย ย ย ย ย ย ย ย ย ย id TEXT PRIMARY KEY NOT NULL,
ย ย ย ย ย ย ย ย ย ย ย ย full_name TEXT,
ย ย ย ย ย ย ย ย ย ย ย ย email TEXT,
ย ย ย ย ย ย ย ย ย ย ย ย google_id TEXT,
ย ย ย ย ย ย ย ย ย ย ย ย onboarding_completed INTEGER DEFAULT 0,
ย ย ย ย ย ย ย ย ย ย ย ย biometric_enabled INTEGER DEFAULT 0,
ย ย ย ย ย ย ย ย ย ย ย ย weight REAL,
ย ย ย ย ย ย ย ย ย ย ย ย height REAL,
ย ย ย ย ย ย ย ย ย ย ย ย birth_date TEXT,
ย ย ย ย ย ย ย ย ย ย ย ย diabetes_condition TEXT,
ย ย ย ย ย ย ย ย ย ย ย ย restriction TEXT,
ย ย ย ย ย ย ย ย ย ย ย ย synced_at TEXT DEFAULT NULL 
ย ย ย ย ย ย ย ย ย ย );`
ย ย ย ย ย ย ย ย );

ย ย ย ย ย ย ย ย // Leituras
ย ย ย ย ย ย ย ย tx.executeSql(
ย ย ย ย ย ย ย ย ย ย `CREATE TABLE IF NOT EXISTS readings (
ย ย ย ย ย ย ย ย ย ย ย ย id TEXT PRIMARY KEY NOT NULL,
ย ย ย ย ย ย ย ย ย ย ย ย measurement_time TEXT,
ย ย ย ย ย ย ย ย ย ย ย ย glucose_level REAL,
ย ย ย ย ย ย ย ย ย ย ย ย meal_context TEXT,
ย ย ย ย ย ย ย ย ย ย ย ย time_since_meal TEXT,
ย ย ย ย ย ย ย ย ย ย ย ย notes TEXT,
ย ย ย ย ย ย ย ย ย ย ย ย synced_at TEXT DEFAULT NULL 
ย ย ย ย ย ย ย ย ย ย );`
ย ย ย ย ย ย ย ย );

ย ย ย ย ย ย ย ย // Metadados de sincronizaรงรฃo
ย ย ย ย ย ย ย ย tx.executeSql(
ย ย ย ย ย ย ย ย ย ย `CREATE TABLE IF NOT EXISTS sync_meta (
ย ย ย ย ย ย ย ย ย ย ย ย key TEXT PRIMARY KEY NOT NULL,
ย ย ย ย ย ย ย ย ย ย ย ย value TEXT
