ย ย ย ย ย ย ย ย );
ย ย ย ย ย ย },
ย ย ย ย ย ย (err) => {
ย ย ย ย ย ย ย ย console.error('initDB - erro transaction:', err);
ย ย ย ย ย ย ย ย reject(err);
ย ย ย ย ย ย },
ย ย ย ย ย ย () => {
ย ย ย ย ย ย ย ย console.log('Banco inicializado com sucesso โ');
ย ย ย ย ย ย ย ย resolve(true);
ย ย ย ย ย ย }
ย ย ย ย );
ย ย });
}

// ----------------------
