/**
ย* Retorna o UID do usuรกrio logado no Firebase Auth.
ย*/
function getFirebaseUID(): string | null {
ย ย return auth.currentUser?.uid || null;
}

/**
ย* Sincroniza o objeto UserProfile com o Cloud Firestore.
ย*/
export async function syncUserProfileToFirestore(profile: UserProfile): Promise<void> {
ย ย const uid = getFirebaseUID();

ย ย if (!uid) {
ย ย ย ย console.warn("Usuรกrio nรฃo autenticado no Firebase. Sincronizaรงรฃo de perfil ignorada.");
ย ย ย ย return;
ย ย }

ย ย try {
ย ย ย ย const userRef = doc(db, 'users', uid);

