import 'react-native-get-random-values';
import React, { useState, useEffect, useRef } from 'react';
import {
    Text,
    TextInput,
    TouchableOpacity,
    StyleSheet,
    Alert,
    ScrollView,
    KeyboardAvoidingView,
    Platform,
    AppState,
    View,
} from 'react-native';
import { AntDesign, Feather } from '@expo/vector-icons';
import { SafeAreaView } from 'react-native-safe-area-context';
import * as SecureStore from 'expo-secure-store';
import * as LocalAuthentication from 'expo-local-authentication';
import * as WebBrowser from 'expo-web-browser';
import * as Google from 'expo-auth-session/providers/google';
import * as Crypto from 'expo-crypto';
import { v4 as uuidv4 } from 'uuid';

WebBrowser.maybeCompleteAuthSession();

// 游눠 Certifique-se de que esta tipagem esteja correta.
interface NavigationProp {
    navigate: (screen: string) => void;
    replace: (screen: string) => void;
}

export default function RegisterScreen({ navigation }: { navigation: NavigationProp }) {
    const [email, setEmail] = useState<string>('');
    const [password, setPassword] = useState<string>('');
    const [confirmPassword, setConfirmPassword] = useState<string>('');
    const [showPassword, setShowPassword] = useState<boolean>(false);
    const [showConfirmPassword, setShowConfirmPassword] = useState<boolean>(false);
    const [emailError, setEmailError] = useState<string>('');
    const [passwordError, setPasswordError] = useState<string>('');
    const [confirmPasswordError, setConfirmPasswordError] = useState<string>('');
    const [isButtonDisabled, setIsButtonDisabled] = useState<boolean>(true);

    const appState = useRef(AppState.currentState);
    const lastActiveTime = useRef<number>(Date.now());

    // --- Google OAuth Config
    const [request, response, promptAsync] = Google.useAuthRequest({
        expoClientId:
            '360317541807-i8qgcvkit3vsv8s7did5rgjod17eld77.apps.googleusercontent.com',
        webClientId:
            '360317541807-i8qgcvkit3vsv8s7did5rgjod17eld77.apps.googleusercontent.com',
        androidClientId:
            'SUA_ANDROID_CLIENT_ID_AQUI.apps.googleusercontent.com', // 丘멆잺 Aten칞칚o: Substitua este Client ID
        scopes: ['profile', 'email'],
    });

    // --- Handle Google Response
    useEffect(() => {
        const handleGoogleAuth = async () => {
            if (response?.type === 'success' && response.authentication) {
                try {
                    const token = response.authentication.accessToken;
                    await SecureStore.setItemAsync('google_token', token);

                    const userInfoResponse = await fetch(
                        'https://www.googleapis.com/userinfo/v2/me',
                        {
                            headers: { Authorization: `Bearer ${token}` },
                        }
                    );
                    const user = await userInfoResponse.json();

                    const profile = {
                        id: uuidv4(),
                        full_name: user.name || 'Usu치rio Google',
                        email: user.email,
                        provider: 'google',
                        createdAt: new Date().toISOString(),
                        birth_date: new Date(1990, 0, 1).toISOString(),
                        height: null,
                        weight: null,
                        restriction: '',
                    };

                    await SecureStore.setItemAsync('registered_email', user.email);
                    await SecureStore.setItemAsync('user_profile', JSON.stringify(profile));
                    await SecureStore.setItemAsync('biometric_enabled', 'false');

                    Alert.alert('Sucesso', 'Conta criada com Google!');
                    navigation.replace('ProfileSetup');
                } catch (err) {
                    console.error('Erro ao registrar com Google:', err);
                    Alert.alert('Erro', 'N칚o foi poss칤vel registrar com Google.');
                }
            } else if (response?.type === 'error') {
                Alert.alert('Erro', 'O login com Google falhou ou foi cancelado.');
                console.log('Resposta de erro do Google:', response);
            }
        };

        handleGoogleAuth();
    }, [response]);

    // --- Fun칞칚o para chamar o prompt do Google
    const handleGoogleSignIn = async () => {
        try {
            await promptAsync();
        } catch (error) {
            console.error('Erro ao iniciar o prompt do Google:', error);
            Alert.alert('Erro', 'N칚o foi poss칤vel iniciar o login com Google.');
        }
    };

    // --- Validadores
    const validateEmail = (email: string) => /\S+@\S+\.\S+/.test(email);
    const validatePassword = (pw: string) =>
        pw.length >= 8 &&
        /[A-Z]/.test(pw) &&
        /[a-z]/.test(pw) &&
        /\d/.test(pw) &&
        /[@$!%*?&]/.test(pw);

    // --- Registro Manual
    const handleRegister = async () => {
        try {
            setEmailError('');
            setPasswordError('');
            setConfirmPasswordError('');

            if (!email || !password || !confirmPassword) {
                return Alert.alert('Campos obrigat칩rios', 'Preencha todos os campos.');
            }
            if (!validateEmail(email)) {
                setEmailError('Digite um e-mail v치lido.');
                return;
            }
            if (!validatePassword(password)) {
                setPasswordError('A senha deve ter 8+ caracteres, Mai칰scula, min칰scula, n칰mero e s칤mbolo.');
                return;
            }
            if (password !== confirmPassword) {
                setConfirmPasswordError('As senhas n칚o coincidem.');
                return;
            }

            const passwordHash = await Crypto.digestStringAsync(
                Crypto.CryptoDigestAlgorithm.SHA256,
                password
            );

            const profile = {
                id: uuidv4(),
                full_name: 'Usu치rio GlucoCare',
                email: email.trim(),
                provider: 'manual',
                createdAt: new Date().toISOString(),
                birth_date: new Date(1990, 0, 1).toISOString(),
                height: null,
                weight: null,
                restriction: '',
            };

            await SecureStore.setItemAsync('registered_email', email.trim());
            await SecureStore.setItemAsync('registered_password', passwordHash);
            await SecureStore.setItemAsync('user_profile', JSON.stringify(profile));

            try {
                const hasHardware = await LocalAuthentication.hasHardwareAsync();
                const supported =
                    await LocalAuthentication.supportedAuthenticationTypesAsync();
                await SecureStore.setItemAsync(
                    'biometric_enabled',
                    hasHardware && supported.length > 0 ? 'true' : 'false'
                );
            } catch {
                await SecureStore.setItemAsync('biometric_enabled', 'false');
            }

            Alert.alert('Sucesso', 'Conta criada com sucesso!');
            navigation.replace('ProfileSetup');
        } catch (err) {
            console.error('handleRegister - erro:', err);
            Alert.alert('Erro', 'N칚o foi poss칤vel registrar sua conta.');
        }
    };

    // --- Sess칚o expira ap칩s 5 min
    useEffect(() => {
        const subscription = AppState.addEventListener('change', (nextState) => {
            if (appState.current === 'active' && nextState === 'background') {
                lastActiveTime.current = Date.now();
            }

            if (
                appState.current.match(/background|inactive/) &&
                nextState === 'active'
            ) {
                const inactiveTime = Date.now() - lastActiveTime.current;
                if (inactiveTime > 5 * 60 * 1000) {
                    Alert.alert('Sess칚o expirada', 'Fa칞a login novamente.');
                    navigation.replace('Login');
                }
            }

            appState.current = nextState;
        });

        return () => subscription.remove();
    }, []);

    // --- Desabilitar bot칚o de registro at칠 que todos os campos sejam v치lidos
    useEffect(() => {
        const isFormValid =
            validateEmail(email) &&
            validatePassword(password) &&
            password === confirmPassword;
        setIsButtonDisabled(!isFormValid);
    }, [email, password, confirmPassword]);

    return (
        <SafeAreaView style={styles.safe}>
            <KeyboardAvoidingView
                style={{ flex: 1 }}
                behavior={Platform.OS === 'ios' ? 'padding' : undefined}
            >
                <ScrollView
                    contentContainerStyle={styles.container}
                    keyboardShouldPersistTaps="handled"
                >
                    <AntDesign
                        name="adduser"
                        size={64}
                        color="#2563eb"
                        style={{ alignSelf: 'center', marginBottom: 20 }}
                    />

                    <Text style={styles.title}>Criar Conta</Text>
                    <Text style={styles.subtitle}>
                        Preencha os dados ou use sua conta Google
                    </Text>

                    {/* Bot칚o Google */}
                    <TouchableOpacity
                        style={[styles.googleButton, !request && { opacity: 0.5 }]}
                        disabled={!request}
                        onPress={handleGoogleSignIn}
                    >
                        <AntDesign
                            name="google"
                            size={20}
                            color="#DB4437"
                            style={{ marginRight: 8 }}
                        />
                        <Text style={styles.googleText}>Cadastrar com Google</Text>
                    </TouchableOpacity>

                    <Text style={styles.orText}>ou</Text>

                    {/* Inputs */}
                    <TextInput
                        style={[styles.input, emailError ? styles.inputError : null]}
                        placeholder="E-mail"
                        autoCapitalize="none"
                        keyboardType="email-address"
                        value={email}
                        onChangeText={setEmail}
                    />
                    {emailError ? <Text style={styles.errorText}>{emailError}</Text> : null}

                    <View style={styles.passwordContainer}>
                        <TextInput
                            style={[styles.input, passwordError ? styles.inputError : null, { paddingRight: 50 }]}
                            placeholder="Senha"
                            secureTextEntry={!showPassword}
                            value={password}
                            onChangeText={setPassword}
                        />
                        <TouchableOpacity
                            onPress={() => setShowPassword(!showPassword)}
                            style={styles.eyeIcon}
                        >
                            <Feather
                                name={showPassword ? 'eye' : 'eye-off'}
                                size={24}
                                color="#6b7280" // Cor preta para o 칤cone
                            />
                        </TouchableOpacity>
                    </View>
                    {passwordError ? (
                        <Text style={styles.errorText}>{passwordError}</Text>
                    ) : null}

                    <View style={styles.passwordContainer}>
                        <TextInput
                            style={[styles.input, confirmPasswordError ? styles.inputError : null, { paddingRight: 50 }]}
                            placeholder="Confirmar Senha"
                            secureTextEntry={!showConfirmPassword}
                            value={confirmPassword}
                            onChangeText={setConfirmPassword}
                        />
                        <TouchableOpacity
                            onPress={() => setShowConfirmPassword(!showConfirmPassword)}
                            style={styles.eyeIcon}
                        >
                            <Feather
                                name={showConfirmPassword ? 'eye' : 'eye-off'}
                                size={24}
                                color="#6b7280"
                            />
                        </TouchableOpacity>
                    </View>
                    {confirmPasswordError ? (
                        <Text style={styles.errorText}>{confirmPasswordError}</Text>
                    ) : null}

                    <TouchableOpacity
                        style={[styles.buttonPrimary, isButtonDisabled && { opacity: 0.5 }]}
                        onPress={handleRegister}
                        disabled={isButtonDisabled}
                    >
                        <Text style={styles.buttonText}>Registrar</Text>
                    </TouchableOpacity>

                    <Text style={styles.loginText}>
                        J치 tem uma conta?{' '}
                        <Text
                            style={styles.loginLink}
                            onPress={() => navigation.navigate('Login')}
                        >
                            Entrar
                        </Text>
                    </Text>
                </ScrollView>
            </KeyboardAvoidingView>
        </SafeAreaView>
    );
}

