import React, { createContext, useContext, useEffect, useState, ReactNode } from 'react';
import { User as FirebaseUser, onAuthStateChanged, signInWithEmailAndPassword, createUserWithEmailAndPassword, signOut } from 'firebase/auth';
import { auth } from '../config/firebase';
import { initDB, saveOrUpdateUser, getUser, UserProfile, clearUser } from '../services/dbService';
import * as Google from 'expo-auth-session/providers/google';
import * as WebBrowser from 'expo-web-browser';
import { Alert, Text } from 'react-native'; // ✅ IMPORTANDO O TEXT PARA EVITAR ERRO

WebBrowser.maybeCompleteAuthSession();

// Tipo de contexto de autenticação
interface AuthContextType {
    user: FirebaseUser | null;
    profile: UserProfile | null;
    isAuthenticated: boolean;
    isLoading: boolean;
    login: (email: string, password: string) => Promise<void>;
    register: (email: string, password: string, name: string) => Promise<void>;
    logout: () => Promise<void>;
    loginWithGoogle: () => Promise<void>;
    updateProfileLocally: (updatedProfile: UserProfile) => void;
}

const AuthContext = createContext<AuthContextType | undefined>(undefined);

// Função para inicializar ou carregar o perfil do usuário
async function handleUserInitialization(firebaseUser: FirebaseUser, name?: string): Promise<UserProfile | null> {
    try {
        const existingProfile = await getUser();
        if (existingProfile && existingProfile.id === firebaseUser.uid) {
            console.log("Perfil local encontrado.");
            return existingProfile;
        }

        // Cria um novo perfil se não existir
        const newProfile: UserProfile = {
            id: firebaseUser.uid,
            name: name || firebaseUser.displayName || 'Usuário GlucoCare',
            email: firebaseUser.email || '',
            googleId: '', 
            onboardingCompleted: false,
            biometricEnabled: false,
            weight: null,
            height: null,
            birthDate: '',
            condition: '',
            restriction: '',
            syncedAt: null,
        };
        const savedProfile = await saveOrUpdateUser(newProfile); 
        console.log("Novo perfil criado e sincronizado.");
        return savedProfile as UserProfile;

    } catch (error) {
        console.error("Erro ao carregar/criar perfil", error);
        Alert.alert("Erro de Dados", "Não foi possível carregar ou criar o perfil do usuário localmente. Tente novamente.");
        return null;
    }
}

// Provê contexto de autenticação
interface AuthProviderProps {
    children: ReactNode;
}

export const AuthProvider: React.FC<AuthProviderProps> = ({ children }) => {
    const [user, setUser] = useState<FirebaseUser | null>(null);
    const [profile, setProfile] = useState<UserProfile | null>(null);
    const [isLoading, setIsLoading] = useState(true); 

    // Inicializa o banco de dados local
    useEffect(() => {
        initDB().catch(err => console.error("Erro ao inicializar DB", err));
    }, []);

    // Gerencia o estado de autenticação e perfil
    useEffect(() => {
        const unsubscribe = onAuthStateChanged(auth, async (firebaseUser) => {
            if (firebaseUser) {
                try {
                    const userProfile = await handleUserInitialization(firebaseUser);
                    if (userProfile) {
                        setUser(firebaseUser);
                        setProfile(userProfile);
                    } else {
                        await signOut(auth); // Força logout em caso de erro
                    }
                } catch (error) {
                    console.error("Erro ao carregar perfil após login:", error);
                    await signOut(auth); 
                }
            } else {
                setUser(null);
                setProfile(null);
            }
            setIsLoading(false); 
        });

        return () => unsubscribe();
    }, []);

    // Funções de autenticação
    const login = async (email: string, password: string) => {
        setIsLoading(true);
        try {
            await signInWithEmailAndPassword(auth, email, password);
        } catch (error: any) {
            console.error("Erro ao fazer login:", error);
            setIsLoading(false);
            throw error;
        }
    };

    const register = async (email: string, password: string, name: string) => {
        setIsLoading(true);
        try {
            const userCredential = await createUserWithEmailAndPassword(auth, email, password);
            const userProfile = await handleUserInitialization(userCredential.user, name);
            setUser(userCredential.user);
            setProfile(userProfile);
        } catch (error: any) {
            console.error("Erro ao registrar:", error);
            setIsLoading(false);
            throw error;
        }
    };

    const updateProfileLocally = (updatedProfile: UserProfile) => {
        setProfile(updatedProfile);
    };

    const loginWithGoogle = async () => {
        console.warn("Google Auth não implementado.");
    };

    const logout = async () => {
        try {
            setUser(null);
            setProfile(null);
            await clearUser();
            await signOut(auth);
        } catch (error) {
            console.error("Erro ao fazer logout:", error);
            throw error;
        }
    };

    const isAuthenticated = !!user && !!profile;

    return (
        <AuthContext.Provider value={{ user, profile, isAuthenticated, isLoading, login, register, logout, loginWithGoogle, updateProfileLocally }}>
            {children}
        </AuthContext.Provider>
    );
};

export const useAuth = () => {
    const context = useContext(AuthContext);
    if (context === undefined) {
        throw new Error('useAuth deve ser usado dentro de um AuthProvider');
    }
    return context;
};
